(()=>{"use strict";class t{constructor(t){this.length=t,this.ship=this.createShip()}getShipLength(){return this.ship.length}createShip(){let t=[];for(let e=0;e<this.length;e++)t.push({hit:!1});return t}hit(t){this.ship[t].hit=!0}checkHit(t){return!!t.hit}isSunk(){return this.ship.every(this.checkHit)}}new t(5),new t(4),new t(3),new t(3),new t(2),new t(5),new t(4),new t(3),new t(3),new t(2)})();